create table COMMON_CODE
(
    CODE_ID              VARCHAR2(10)  not null,
    GROUP_ID             VARCHAR2(100) not null
        constraint FK_COMMON_CODE_GROUP_TO_COMMON_CODE_1
            references COMMON_CODE_GROUP,
    CODE_NAME            VARCHAR2(200) not null,
    CODE_NUMBER          VARCHAR2(4),
    USE_YN               CHAR          not null,
    SYSTEM_CREATE_DATE   DATE          not null,
    SYSTEM_CREATE_USERID VARCHAR2(32)  not null,
    SYSTEM_UPDATE_DATE   DATE,
    SYSTEM_UPDATE_USERID VARCHAR2(32),
    constraint PK_COMMON_CODE
        primary key (CODE_ID, GROUP_ID)
)
/

